  for (int i = 1; i <= n; i++) cin >> a[i];
  build(1, n);
  for (int i = 1, l, r, x; i <= m; i++) {
    cin >> l >> r >> x;
    modify(1, l, r, x);
    while (!p[1].todo.first) resolve(1, p[1].todo.second);
    cout << ans << '\n';
  }
}
