  friend inline info operator^(const info &a, const info &b) {
    static info s;
    s.n = a.n * b.n;
    s.a = 2 * a.a * b.a + a.a * b.b + a.b * b.a;
    s.b = a.b * b.b;
    s.c = a.c * b.c + a.c * (b.n - b.c) + b.c * (a.n - a.c);
